CREATE TABLE issuing_body(
   id serial primary key,
   abbreviation varchar(8) not null,
   name varchar(128) not null,
   type integer not null -- 1 Orgão Federal -- 2 Orgão Regional -- 3 Orgão Municipal -- 4 Estrangeiro / Outro
);

CREATE INDEX idx_sigla ON issuing_body (abbreviation);
CREATE INDEX idx_nome  ON issuing_body (name);

